import logo from "./bp32PizJ.png";
import "./App.css";
import React from "react";
import web3 from './Pages/web3';
import lottery from "./Pages/lottery";

import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
const MySwal = withReactContent(Swal);


class App extends React.Component {
  constructor(){
    super();
    this.state={
      balance:'',
      Players:[],
      accounts:[],
      amtToPay:null
    }
  }

  componentDidMount(){
    this.myfunc();
  }
  myfunc=async()=>{
    const accounts = await web3.eth.getAccounts();
    // .then((res)=>{
    //   console.log(res)
    // });
    const Players = await lottery.methods.getPlayers().call();
    const balance = await web3.eth.getBalance(lottery._address);
    this.setState({balance, Players, accounts})
    // var t=await lottery.methods.manager().call();
    // console.log(t)
  }


  render() {

    console.log(this.state)
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo"  style={{width:'8rem',height:'10rem'}}/>
          <p>
            <code>Contract Wallet: <span className={web3.utils.fromWei(this.state.balance, 'ether') ==0? 'danger':'safe'}>{web3.utils.fromWei(this.state.balance, 'ether')}</span> Ether.</code>
          </p>
            <div className="root-form-box">
              <form>
              <div className="row">
                <div className="col-md-12 d-flex justify-content-center">
                  <div className="walletForm">
                      <div className="row">
                          <div className="col-md-6">
                            <code className="text-white">Ether to Send</code>
                          </div>
                          <div className="col-md-6">
                              <input type="number" placeholder="Enter Ether" value={this.state.amtToPay} onChange={(e)=>this.setState({...this.state,amtToPay:e.target.value})} className="form-control" ></input>
                          </div>
                      </div>
                      <div className="row mt-2">
                          <div className="col-md-12">
                              <button className="btn btn-success m4-5" style={{float:'right'}}>Send</button>
                              <button className="btn btn-warning" style={{float:'right'}}>Pick a Winner</button>
                          </div>
                      </div>
                  </div>
                </div>
              </div>
            </form>
            </div>
        </header>
      </div>
    );
  }
}
export default App;
