  constructor(){
    super();
    this.state={
      balance:'',
      Players:[],
      accounts:[]
    }
  }

  componentDidMount(){
    this.myfunc();
  }
  myfunc=async()=>{
    const accounts = await web3.eth.getAccounts();
    // .then((res)=>{
    //   console.log(res)
